def papers_get(reviewed_by=None, query=None, page=None, per_page=None, sort_by=None, sort_order=None):  # noqa: E501
    """Get paginated collections of papers, optionally filtered by reviewing service

     # noqa: E501

    :param reviewed_by: The IDs of the reviewing services for which papers are requested.
    :type reviewed_by: List[str]
    :param query: A search string to filter the results by.
    :type query: str
    :param page: The page number of the results to retrieve. The first page is 1.
    :type page: int
    :param per_page: The number of results to return per page.
    :type per_page: int
    :param sort_by: The field to sort the results by.
    :type sort_by: str
    :param sort_order: The direction to sort the results in.
    :type sort_order: str

    :rtype: InlineResponse200
    """
    sort_by_attr = {
        "preprint-date": "pub_date",
        "reviewing-date": "revdate"
    }
    assert sort_by in sort_by_attr.keys()

    def sort_key(paper):
        attr = sort_by_attr[sort_by]
        return paper[attr]

    def filter_by_review_service(paper):
        filter_active = reviewed_by and len(reviewed_by) > 0
        if not filter_active:
            return True
        return any([service in paper["reviewed_by"] for service in reviewed_by])

    def filter_by_query(paper):
        filter_active = query and len(query) > 0
        if not filter_active:
            return True
        query_ci = query.lower()
        return (
            query_ci in paper["doi"].lower()
            or query_ci in paper["title"].lower()
            or query_ci in paper["abstract"].lower()
        )
    def filter_papers(paper):
        return filter_by_query(paper) and filter_by_review_service(paper)

    papers = list(filter(filter_papers, all_papers))
    total_items = len(papers)
    total_pages = total_items // per_page + 1

    papers = sorted(papers, key=sort_key)
    if sort_order == "desc":
        papers = reversed(papers)

    papers = list(papers)[per_page * (page - 1):per_page * page]
    base_url = f"/api/v2/papers?sort_by={sort_by}&sort_order={sort_order}&per_page={per_page}"
    return {
        "items": papers,
        "paging": {
            "first": f"{base_url}&page=1",
            "prev": f"{base_url}&page={page - 1}" if page > 1 else None,
            "current": f"{base_url}&page={page}",
            "next": f"{base_url}&page={page + 1}" if page < total_pages else None,
            "last": f"{base_url}&page={total_pages}",
            "currentPage": page,
            "perPage": per_page,
            "totalItems": total_items,
            "totalPages": total_pages,
            "sortedBy": sort_by,
            "sortedOrder": sort_order,
        }
    }

